import React, {Fragment, ReactNode, useState} from 'react';
import {Dialog, Transition} from "@headlessui/react";
import {DialogProps} from "../components/type/type";

interface WindowDialogProps extends DialogProps {
  children: ReactNode | string;
  title?: ReactNode | string;
  footer? : ReactNode | string;
}

// TODO 키보드 ESC 누르면 닫는 기능 추가할 것
const WindowDialog = ({show, children, title, footer}: WindowDialogProps) => {
  return (
    <Transition appear show={show} as={Fragment}>
      <Dialog
        as="div" className="relative z-10 bg-amber-800"
        onClose={() => console.log()}
      >
        <Transition.Child
          as={Fragment}
          enter="ease-out duration-300"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="ease-in duration-200"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <div className="fixed inset-0 bg-black bg-opacity-25"/>
        </Transition.Child>

        <div className="fixed inset-0 overflow-y-auto">
          <div className="flex min-h-full items-center justify-center p-4 text-center">
            <Transition.Child
              as={Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0 scale-95"
              enterTo="opacity-100 scale-100"
              leave="ease-in duration-200"
              leaveFrom="opacity-100 scale-100"
              leaveTo="opacity-0 scale-95"
            >
              <Dialog.Panel
                className="w-full max-w-md transform overflow-hidden rounded-2xl bg-white p-6 text-left align-middle shadow-xl transition-all">
                <Dialog.Title
                  as="h3"
                  className="text-lg font-medium leading-6 text-gray-900"
                >
                  {title}
                </Dialog.Title>
                <div className="mt-2">
                  {children}
                </div>
                <div className="mt-4 text-center">
                  {footer}
                </div>
              </Dialog.Panel>
            </Transition.Child>
          </div>
        </div>
      </Dialog>
    </Transition>
  );
};

export default WindowDialog;
